import { faXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import logo from "../../assets/images/vecteezy_jt-logo-monogram-with-slash-style-design-template_.png";
import "./Navbar.css";

const Navbar = () => {
  const logoOnHover = () => {
    let logoImg = document.querySelector(".navbar .logo img");
    logoImg.style.animation = "";
    setTimeout(() => {
      logoImg.style.animation = "rotate-logo 1s ease-in-out";
    }, 0);
  };

  const collapseSidebar = (collapse) => (e) => {
    e.preventDefault();
    const div = document.querySelector(".sidebar");
    if (div.style.display !== "none")
      div.style.width = collapse ? "370px" : "0";
  };

  const links = [
    { path: "/blogs", content: "Explore" },
    { path: "/create", content: "Write" },
  ];


  return (
    <>
      <nav className="blublu">
        <div className="navbar-left">
          <div onMouseOver={logoOnHover} className="logo">
            <img
              onClick={collapseSidebar(true)}
              src={logo}
              alt="logo"
              width="48"
            />
            <Link title="Home" to="/">
              Joov Tek
            </Link>
          </div>
          <Options className="options" />
        </div>
        <div className="sign">
          <Link to="/signin" className="signin">
            Sign in
          </Link>
          <Link to="/singup" className="signup">
            Sign up
          </Link>
        </div>
      </nav>
      <div className="sidebar">
        <button type="button" onClick={collapseSidebar(false)}>
          <FontAwesomeIcon icon={faXmark} />
        </button>
        <Options />
      </div>
    </>
  );
};

export default Navbar;
