import "./Login.css";

const Login = () => {
  const onSubmit = (e) => {
    e.preventDefault();
  };

  const animateBorderBottom = (e) => {
    let p = e.target.parentElement;
    p.style.animation = "grow-width 500ms ease-in-out both";
    console.log('here');
  };

  return (
    <div className="login-card" style={{ animation: "appear 700ms ease-out" }}>
      <h1 className="logo">Joov Tek</h1>
      <form onSubmit={onSubmit}>
        <p className="username">
          <label htmlFor="#username">Username</label>
          <br />
          <input onFocus={animateBorderBottom} type="text" id="username" name="username" />
        </p>
        <p className="password">
          <label htmlFor="#password">Password</label>
          <br />
          <input onFocus={animateBorderBottom} type="password" id="password" name="password" />
        </p>
        <div>
          <button type="submit">Log in</button>
        </div>
      </form>
    </div>
  );
};

export default Login;
