import "./Login.css";
import { Link } from "react-router-dom";
import encrypt from "../../encrypt";

const Login = () => {
  const onFocus = () => {
    let error = document.querySelector(".error");
    error.style.display = "none";
  }

  const onSubmit = (e) => {
    let form = e.currentTarget;
    let pwd = form.password;
    let usr = form.username.value;
    e.preventDefault();

    fetch(`http://localhost:3001/users?username=${usr}&password=${encrypt(pwd.value)}`)
      .then((res) => {
        // invalid username/password
        if (!res.ok) {

          let error = document.querySelector(".error");
          error.style.display = "block";
          throw Error(`${usr}Invalid username or password`);
        }
      }).catch(err => {
        console.log(err.message);
      });
  };

  return (
    <div className="login-card" style={{ animation: "appear 700ms ease-out" }}>
      <h1 className="logo">Joov Tek</h1>
      <form onSubmit={onSubmit}>
        <div className="username">
          <label htmlFor="#username">Username</label>
          <br />
          <input type="text" id="username" name="username" onFocus={onFocus} />
          <div className="line"></div>
        </div>
        <div className="password">
          <label htmlFor="#password">Password</label>
          <br />
          <input type="password" id="password" name="password" onFocus={onFocus} />
          <div className="line"></div>
        </div>
        <p className="error">Invalid username or password!</p>
        <button type="submit">Log in</button>
        <p className="forgot">
          <Link to="/password">forgot password?</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
