import "./Login.css";
import { Link } from "react-router-dom";
import encrypt from "../../encrypt";

const Login = () => {
  const [displayErr, setDisplayErr] = useState(false);

  const onFocus = () => {
    let error = document.querySelector(".error");
    error.style.display = "none";
  };

  const onSubmit = (e) => {
    let form = e.currentTarget;
    let error = document.querySelector(".error");
    let pwd = encrypt(form.password);
    let usr = form.username.value;

    form.password.value = "";
    e.preventDefault();

    const displayError = message => {
      setDisplayErr(true);
      error.innerHTML = message;
      throw Error(message);
    }

    fetch(`http://localhost:3001/users?username=${usr}`)
      .then((res) => {
        console.log(res);
        if (!res.ok)
          displayError("Invalid username!"); 
        return res.json();
      })
      .then((data) => {
        let user = data[0];
        if (pwd !== user.password) 
          displayError("Invalid password!");
      })
      .catch((err) => {
        console.log(error.message);
      });
  };

  return (
    <div className="login-card" style={{ animation: "appear 700ms ease-out" }}>
      <h1 className="logo">Joov Tek</h1>
      <form onSubmit={onSubmit}>
        <div className="username">
          <label htmlFor="#username">Username</label>
          <br />
          <input type="text" id="username" name="username" onFocus={onFocus} />
          <div className="line"></div>
        </div>
        <div className="password">
          <label htmlFor="#password">Password</label>
          <br />
          <input
            type="password"
            id="password"
            name="password"
            onFocus={onFocus}
          />
          <div className="line"></div>
        </div>
        {displayErr && <p className="error"></p>}
        <button type="submit">Log in</button>
        <p className="forgot">
          <Link to="/password">forgot password?</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
