import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react/cjs/react.development";
import "./Home.css";

const Home = () => {
  const [author, setAuthor] = useState("");
  const [content, setContent] = useState("");

  fetch("http://api.quotable.io/random")
    .then(res => res.json())
    .then(data => {
      setAuthor(data.author);
      setContent(data.content);
    })

  setTimeout(async () => {
    let res = await fetch("http://api.quotable.io/random");
    let data = await res.json();
    setAuthor(data.author);
    setContent(data.content);
    },
    content.lentgh*1000 + 5000
  );

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>
          Random inspirational quote for you
        </span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      <TypeWriter
        options={{
          loop: true,
          cursor: "_",
          cursorClassName: "cursor",
          wrapperClassName: "typewriter",
        }}
        onInit={(typewriter) => {
          typewriter.typeString(`${content}`).pauseFor(5000).start();
        }}
      />
      <p className="author">â€” {author}</p>
    </div>
  );
};

export default Home;
