import "./Login.css";
import { Link } from "react-router-dom";
import encrypt from "../../encrypt";

const Login = () => {
  const onFocus = () => {
    let error = document.querySelector(".error");
    error.style.display = "none";
  };

  const onSubmit = (e) => {
    let form = e.currentTarget;
    let error = document.querySelector(".error");
    let pwd = encrypt(form.password);
    let usr = form.username.value;

    form.password.value = "";
    e.preventDefault();

    fetch(`http://localhost:3001/users?username=${usr}`)
      .then((res) => {
        if (!res.ok) {
          const message = "Invalid username!";
          error.innerHTML = message;
          error.style.display = "block";
          console.log("here");
          throw Error(message);
        }
        return res.json();
      })
      .then((data) => {
        let user = data[0];
        if (pwd !== user.password) {
          const message = "Invalid password!";
          error.innerHTML = message;
          error.style.display = "block";
          throw Error(message);
        }
      })
      .catch((err) => {
        console.log(error.message);
      });
  };

  return (
    <div className="login-card" style={{ animation: "appear 700ms ease-out" }}>
      <h1 className="logo">Joov Tek</h1>
      <form onSubmit={onSubmit}>
        <div className="username">
          <label htmlFor="#username">Username</label>
          <br />
          <input type="text" id="username" name="username" onFocus={onFocus} />
          <div className="line"></div>
        </div>
        <div className="password">
          <label htmlFor="#password">Password</label>
          <br />
          <input
            type="password"
            id="password"
            name="password"
            onFocus={onFocus}
          />
          <div className="line"></div>
        </div>
        <p className="error"></p>
        <button type="submit">Log in</button>
        <p className="forgot">
          <Link to="/password">forgot password?</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
