import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import "./Square.css";

const randint = (min, max) => Math.ceil(Math.random() * (max - min) + min);

export default function Square({
  side = randint(32, 64),
  colors = ["grey", "black"],
}) {
  const sideMin = (5 * side) / 6;
  const [hide, setHide] = useState(true);
  document.addEventListener("click", () => {
    setHide(!hide);
  });

  return (
    <div
      style={{
        width: `${side}px`,
        height: `${side}px`,
        transform: `rotate(${randint(0, 361)}deg)`,
        position: "relative",
        animation: `lr-${randint(1, 4)} 700ms ease-out 1s forwards`
      }}
      className="square"
      hidden={hide}
    >
      <div
        style={{
          width: `${sideMin}px`,
          height: `${sideMin}px`,
          border: `2px solid ${colors[0]}`,
        }}
      ></div>
      <div
        style={{
          width: `${sideMin}px`,
          height: `${sideMin}px`,
          background: colors[1],
          position: "absolute",
          bottom: 0,
          right: 0,
        }}
      ></div>
    </div>
  );
}
