import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react/cjs/react.development";
import "./Home.css";

const Home = () => {
  const [author, setAuthor] = useState("Bill Gates");
  const [content, setContent] = useState("Hello world! My name is ACF");

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro"><span style={{ marginRight: "1rem" }}>Random inspirational quote for you</span><FontAwesomeIcon icon={faMugHot} /></p>
      <TypeWriter
        options={{
          loop: true,
          cursor: '_',
          cursorClassName: "cursor",
          wrapperClassName: "typewriter",
        }}
        onInit={(typewriter) => {
          typewriter
            .typeString(`${content}`)
            .start();
        }}
      />
      <p className="author">â€”{author}</p>
    </div>
  );
};

export default Home;
