import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import { useEffect } from "react";
import useFetch from "../../hooks/useFetch";
import "./Home.css";

const Home = () => {
  const delay = 500;
  const pause = 3000;
  const randint = (min, max) => Math.random() * (max - min) + min;

  let contents  = [];
  let authors   = [];

  fetch(`http://api.quotable.io/quotes?tags=technology&page=${randint(1, 4)}`)
    .then(res => res.json())
    .then(datas => {
      contents = datas.result.map(obj => obj.content);
      authors = datas.result.map(obj => obj.author);    
    })  
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const text = contents[index];
    setInterval(() => {
      setIndex(i => (i + 1) % text.length);
    }, (text.length * delay * 3) / 2 + pause);
  }, []);

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      {error && <h1>Failed to fetch quote!</h1>}
      {!(isPending || error) && (
        <div className="quote">
          <TypeWriter
            options={{
              cursor: "_",
              cursorClassName: "cursor",
              wrapperClassName: "typewriter",
              delay: delay,
              pauseFor: pause,
              strings: contents,
            }}
          />
          <p className="author">â€” {authors[index]}</p>
        </div>
      )}
    </div>
  );
};

export default Home;
