import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import "./Square.css";

const randint = (min, max) => Math.floor(Math.random() * (max - min) + min);
let instance = 0;

export default function Square({
  side = randint(16, 64),
  colors = ["grey", "black"],
}) {
  const sideMin = (5 * side) / 6;
  const [hide, setHide] = useState(true);
  document.addEventListener("click", () => {
    setHide(false);
  });

  useEffect(() => { instance++; })

  return (
    <div
      style={{
        width: 0,
        height: 0,
      }}
    >
      <style>
        {`
        @keyframes lr {
          100% {
            transform: translateX(-600px) rotate(${randint(0, 361)});
          }
        }
        `}
      </style>
      <div
        style={{
          width: `${side}px`,
          height: `${side}px`,
          position: "relative",
          animation: `lr 700ms cubic-bezier(0.57, 0.88, 0.04, 1.23) forwards`,
          zIndex: -1,
          opacity: 0.6,
          left: "100vw",
        }}
        className="square"
        hidden={hide}
      >
        <div
          style={{
            width: `${sideMin}px`,
            height: `${sideMin}px`,
            border: `2px solid ${colors[0]}`,
          }}
        ></div>
        <div
          style={{
            width: `${sideMin}px`,
            height: `${sideMin}px`,
            background: colors[1],
            position: "absolute",
            bottom: 0,
            right: 0,
          }}
        ></div>
      </div>
    </div>
  );
}
