import useFetch from "../../hooks/useFetch";
import BlogList from "../BlogList/BlogList";
import "./Home.css";

const Home = () => {
  const {
    datas: blogs,
    setDatas: setBlogs,
    isPending,
    error,
  } = useFetch("http://localhost:3001/blogs?_sort=title");

  const removeBlog = (id) => {
    setBlogs(blogs.filter((blog) => blog.id !== id));
  };

  return (
    <div className="home">
      <svg><g
     label="Calque 1"
     groupmode="layer"
     id="layer1"
     transform="translate(-49.931607,-71.530275)">
    <path
       type="star"
       id="path10"
       sides="5"
       cx="97.517853"
       cy="123.88691"
       r1="52.091503"
       r2="21.107061"
       arg1="-0.83974436"
       arg2="-0.18302004"
       flatsided="false"
       rounded="0"
       randomized="0"
       d="m 132.29691,85.10622 -14.02451,34.9392 26.8754,24.93445 -37.56297,-2.5413 -15.409108,33.26519 -9.190678,-36.50981 -36.398757,-4.37542 31.88282,-20.02301 -7.086562,-35.969351 28.895345,24.134911 z"
       transform-center-x="-3.379721"
       transform-center-y="-7.7466555"
       style="stroke-width:0.26458332"
       transform="matrix(0.86005243,0.49655151,-0.50955985,0.88258354,78.563067,-33.485514)" />
  </g></svg>
      {isPending && <div>Loading...</div>}
      {blogs && (
        <BlogList
          blogs={blogs}
          title={blogs.length ? "All blogs!" : "No blog found"}
          removeBlog={removeBlog}
        />
      )}
      {error && (
        <p
          style={{
            color: "red",
            fontSize: "1.5rem",
          }}
        >
          {error}
        </p>
      )}
    </div>
  );
};

export default Home;
