import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowRight,
  faTriangleExclamation,
} from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { useState } from "react";
import illustration from "../../assets/images/undraw_launching_re_tomg.svg";
import google from "../../assets/images/1534129544.svg";
import facebook from "../../assets/images/icons8-facebook.svg";

import "./Signup.css";

const Signup = () => {
  const [errMessage, setErrMessage] = useState({
    name: "", email: ""
  });

  const formOnSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    fetch("http://localhost:5000/signup", {
      method: "POST",
      body: JSON.stringify({
        name: form.name.value,
        password: form.password.value,
        email: form.email.value,
      }),
      headers: {
        "Content-type": "application/json",
      },
    })
      .then((result) => result.json())
      .then((result) => {
        for (let key of Object.keys(result.errors))
        setErrMessage(result.message);
      })
      .catch((err) => {
        setErrMessage({
          name: "", email: ""
        });
        console.log(err.message);
      });
  };

  return (
    <div className="sign-up">
      <div className="illustration">
        <img src={illustration} alt="launch" />
      </div>
      <div className="content">
        <h1>Sign Up</h1>
        <form onSubmit={formOnSubmit}>
          <div className="body">
            <div className="name">
              <label htmlFor="#name">Name</label>
              <br />
              <input type="text" name="name" id="name" required />
              <br />
              {errMessage.name && (<p class="errMessage">{errMessage.name} <FontAwesome icon={faTriangleExclamation} /></p>)}
            </div>
            <div className="email">
              <label htmlFor="#email">Email</label>
              <br />
              <input type="email" name="email" id="email" required />
              <br />
              {errMessage[1] && (<p class="errMessage">{errMessage[1]} <FontAwesome icon={faTriangleExclamation} /></p>)}
            </div>
            <div className="password">
              <label htmlFor="#password">Password</label>
              <br />
              <input type="password" name="password" id="password" required />
            </div>
          </div>
          <div className="terms">
            <input type="checkbox" name="terms" id="terms" required />
            <label htmlFor="#terms">
              I have read and agree the <Link to="#">terms and conditions</Link>
              .
            </label>
          </div>
          <p>
            <span>Sign up with</span>
          </p>
          <div className="other">
            <button type="button" className="google">
              <img src={google} alt="google" />
              Google
            </button>
            <button type="button" className="facebook">
              <img src={facebook} alt="facebook" />
              Facebook
            </button>
          </div>
          <button type="submit">
            <FontAwesomeIcon icon={faArrowRight} />
          </button>
        </form>
      </div>
    </div>
  );
};

export default Signup;
