import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import useFetch from "../../hooks/useFetch";
import config from "../../config";
import "./Home.css";

const Home = () => {
  const randint = (min, max) => Math.floor(Math.random() * (max - min) + min);

  const [page] = useState(randint(1, 4));
  const { datas, isPending, error } = useFetch(
    `${config.quotesEndpoint}?tags=technology&page=${page}`
  );

  return (
    <div className="home">
      <div className="head">
        <div className="left">
          <h1>Welcome fellow <span style={{color: "#5a5a5a"}}>Jovo</span>!</h1>
          <p className="intro">
            <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
            <FontAwesomeIcon icon={faMugHot} />
          </p>
          {!isPending && (
            <div className="quote">
              <TypeWriter
                options={{
                  strings: !error
                    ? datas.results
                        .sort((a, b) => randint(-1, 2))
                        .map((obj) => obj.content)
                    : ["Write codes that human can understand."],
                  cursor: "_",
                  cursorClassName: "cursor",
                  wrapperClassName: "typewriter",
                  delay: 100,
                  deleteSpeed: 50,
                  pauseFor: 3000,
                  autoStart: true,
                  loop: true,
                }}
              />
            </div>
          )}
        </div>
        div
      </div>
    </div>
  );
};

export default Home;
