import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import { useEffect } from "react";
import useFetch from "../../hooks/useFetch";
import "./Home.css";

const Home = () => {
  const delay = 500;
  const pause = 3000;
  const randint = (min, max) => Math.random() * (max - min) + min;

  const { datas, isPending, error } = useFetch(
    `http://api.quotable.io/quotes?tags=technology&page=${randint(1, 4)}`
  );
  const [index, setIndex] = useState(0);

  const contents = datas.result.map(obj => obj.content);


  const quotes = datas.result.map((obj) => {
    return { content: obj.content, author: obj.author };
  });

  useEffect(() => {
    const text = quotes[index];
    setInterval(() => {
      setIndex(i => (i + 1) % quotes.length);
    }, (text.length * delay * 3) / 2 + pause);
  }, []);

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      {error && <h1>Failed to fetch quote!</h1>}
      {!(isPending || error) && (
        <div className="quote">
          <TypeWriter
            options={{
              cursor: "_",
              cursorClassName: "cursor",
              wrapperClassName: "typewriter",
              delay: delay,
              pauseFor: pause,
              strings: quotes,
            }}
          />
          <p className="author">â€” {quotes[index].author}</p>
        </div>
      )}
    </div>
  );
};

export default Home;
