import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState } from "react";
import { useEffect } from "react";
import "./Home.css";
import { text } from "@fortawesome/fontawesome-svg-core";

const Home = () => {
  const [index, setIndex] = useState(0);
  const [quote, setQuote] = useState({
    authors: [],
    contents: [],
  });
  const [texts, setTexts] = useState(second)

  const options = {
    cursor: "_",
    cursorClassName: "cursor",
    wrapperClassName: "typewriter",
    delay: 500,
    deleteSpeed: 300,
    pauseFor: 3000,
  };

  const randint = (min, max) => Math.random() * (max - min) + min;

  useEffect(
    () =>
      fetch(
        `http://api.quotable.io/quotes?tags=technology&page=${randint(1, 4)}`
      )
        .then((res) => res.json())
        .then((datas) => {
          setTexts(datas.result.map((obj) => obj.content))
        })
        .catch((err) => {
          console.log(err);
        }),
    []
  );

  const incrementIndex = (delay = 2000) => {
    setTimeout(() => {
      if (quote.contents) {
        const text = quote.contents[index];
        setIndex((i) => (i + 1) % text.length);
        incrementIndex(
          (text.length * (options.delay + options.deleteSpeed)) / 2 +
            options.pauseFor
        );
      }
    }, delay);
  };

  useEffect(incrementIndex, []);

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      <div className="quote">
        <TypeWriter options={{ strings: quote.contents, ...options }} />
        <p className="author">â€” {quote.authors[index]}</p>
      </div>
    </div>
  );
};

export default Home;
