import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import { useState, useEffect } from "react/cjs/react.development";
import "./Home.css";

const Home = () => {
  const [author, setAuthor] = useState("");
  const [content, setContent] = useState("Hello world");

  const [quotes, setQuotes] = useState([]);

  const maxLength = 150;
  const delay = 10 * 1000;

  useEffect(
    () =>
      setInterval(() => {
        fetch("http://api.quotable.io/random?tags=technology")
          .then((res) => res.json())
          .then((data) => {
            setAuthor(data.author);
            setContent(data.content);
          });
      }, delay),
    []
  );

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      <div className="quote">
        <TypeWriter
          options={{
            loop: true,
            cursor: "_",
            cursorClassName: "cursor",
            wrapperClassName: "typewriter",
            maxLength: maxLength,
          }}
          onInit={(typewriter) => {
            setInterval(() => {
              typewriter
                .deleteAll()
                .typeString(`${content}`)
                .pauseFor(delay + 3000)
                .start();
            }, delay);
            /*             console.log("here");
            typewriter.typeString(`${content}`).pauseFor(delay).start();
 */
          }}
        />
        <p className="author">â€” {author}</p>
      </div>
    </div>
  );
};

export default Home;
