import TypeWriter from "typewriter-effect";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMugHot } from "@fortawesome/free-solid-svg-icons";
import useFetch from "../../hooks/useFetch";
import "./Home.css";

const Home = () => {
  const randint = (min, max) => Math.random() * (max - min) + min;
  const options = {
    cursor: "_",
    cursorClassName: "cursor",
    wrapperClassName: "typewriter",
    delay: 500,
    deleteSpeed: 300,
    pauseFor: 3000,
  };

  const { data: texts, isPending, error } = useFetch(`http://api.quotable.io/quotes?tags=technology&page=${randint(1, 4)}`);

  return (
    <div className="home">
      <h1>Welcome fellow Jovo!</h1>
      <p className="intro">
        <span style={{ marginRight: "1rem" }}>What inspires ya today ?</span>
        <FontAwesomeIcon icon={faMugHot} />
      </p>
      {error && <h1>there was an error</h1>}
      {!(isPending || error) && (<div className="quote">
        <TypeWriter options={{ strings: texts, ...options }} />
      </div>)}
    </div>
  );
};

export default Home;
