import "./Login.css";
import { Link } from "react-router-dom";

const Login = () => {
  const onSubmit = (e) => {
    let form = e.currentTarget;
    e.preventDefault();

    fetch (`http://locahost:3001/users?username=${form.username.value}&password=${form.password.value}`)
      .then (res => {
      // invalid username/password        
        if (!res.ok) {
          let error = document.querySelector('.error');
          error.style.display = "block";
          throw Error 
        }
      })
  };

  return (
    <div className="login-card" style={{ animation: "appear 700ms ease-out" }}>
      <h1 className="logo">Joov Tek</h1>
      <form onSubmit={onSubmit}>
        <div className="username">
          <label htmlFor="#username">Username</label>
          <br />
          <input type="text" id="username" name="username" />
          <div className="line"></div>
        </div>
        <div className="password">
          <label htmlFor="#password">Password</label>
          <br />
          <input type="password" id="password" name="password" />
          <div className="line"></div>
        </div>
        <p className="error">Invalid username or password!</p>
        <button type="submit">Log in</button>
        <p className="forgot">
          <Link to="/password">forgot password?</Link>
        </p>
      </form>
    </div>
  );
};

export default Login;
