import React from "react";
import "./Square.css";

const randint = (min, max) => Math.random() * (max - min) + min;

export default function Square({
  side = randint(32, 64),
  colors = ["grey", "black"],
}) {
  const sideMin = 5*side/6;
  document.addEventListener("click", () => {setHide(false)});
  return (
    <div
      style={{
        width: `${side}px`,
        height: `${side}px`,
        transform: `rotate(${randint(0, 361)}deg)`,
        position: "relative",
      }}
      className="square"
      hidden
    >
      <div
        style={{
          width: `${sideMin}px`,
          height: `${sideMin}px`,
          border: `2px solid ${colors[0]}`,
        }}
      ></div>
      <div
        style={{
          width: `${sideMin}px`,
          height: `${sideMin}px`,
          background: colors[1],
          position: "absolute",
          bottom: 0,
          right: 0,
        }}
      ></div>
    </div>
  );
}
